import "./art_section_home.css";
import React from "react";
import { Link } from "react-router-dom";
import axios from "axios";

class ArtSectionHome extends React.Component {
  constructor(props) {
    super(props);
    this.state = { paintings_home: [] };
  }

  componentDidMount() {
    let per_page = 2 ;
    const paintingData = async () => {
      const response = await axios.get("http://localhost:5000/painting?limit=${}");
      const json = await response;
      this.setState({ paintings_home: json.data.response });
      console.log(this.state.paintings_home);
    };
    paintingData();
  }

  render() {
    const { paintings_home } = this.state;

    return (
      <section className="home_art_body">
        <h2 className="home_art_head_title">Some Artwork</h2>

        {paintings_home.map((paint, index) => (
          <div className="home_art_parent" key={index}>
            <div className="home_art_container">
              <div className="home_art_img_container">
                <img
                  className="home_art_img"
                  src={"http://localhost:5001/uploads/${paint.image}"}
                  alt="Art"
                />
              </div>

              <div className="home_art_child_container">
                <h3 className="home_art_title">{paint.title}</h3>

                <p className="home_art_paragraph">{paint.description}</p>
              </div>
            </div>

            <div className="home_art_container">
              <div className="home_art_img_container">
                <avtar
                  className="home_art_img"
                  src={"http://localhost:5000/uploads/${paint.image}"}
                  alt="Art"
                />
              </div>

              <div className="home_art_child_container">
                <h3 className="home_art_title">{paint.title}</h3>
                <p className="home_art_paragraph">{paint.description}</p>
              </div>
            </div>
          </div>
        ))}

        <Link to="/gallery" className="home_art_btn">
          Show More
        </Link>
      </section>
    );
  }
}

export default ArtSectionHome;
