import "./art_section_home.css";
import React from "react";
import { Link } from "react-router-dom";
import axios from "axios";

class ArtSectionHome extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      painting: [],
      show: false,
    };
  }

  // constructor() {
  //   super();
  //   this.state = {
  //     dogs: [],
  //     show: false,
  //     buttonText: true,
  //   };
  // }

  componentDidMount = () => {
    axios
      .get("http://localhost:5000/painting")
      .then((res) => {
        console.log(res);
        this.setState({ painting: res.data.message });
      })
      .catch((err) => console.log(err));
    this.setState({
      show: !this.state.show,
      // buttonText: !this.state.buttonText
    });
  };

  render() {
    return (
      <>
        <body className="home_art_body">
          <h2 className="home_art_head_title">Some Artwork</h2>

          <div className="home_art_parent">
            {this.state.show &&
              this.state.painting.map((paint) => (
                // <img
                //   className="border border-success mt-3 w-50 d-inline"
                //   src={dog}
                //   key={dog}
                //   alt="dog_images"
                // />

                <div className="home_art_container">
                  <div className="home_art_img_container">
                    <img
                      className="home_art_img"
                      src="https://placekitten.com/400/400"
                      alt="cat"
                    />
                  </div>

                  <div className="home_art_child_container">
                    <h3 className="home_art_title">Title</h3>
                    <p className="home_art_paragraph">
                      Lorem Ipsum is simply dummy text of <br /> the printing
                      and typesetting industry.
                      <br /> Lorem Ipsum has been the industry's <br />
                      standard dummy text ever since the <br /> 1500s, software
                      like Aldus PageMaker <br /> including versions of Lorem
                      Ipsum.
                      <br />
