import "./gallery_body.css";
import React from "react";
import axios from "axios";

class GalleryBody extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      galleries: [],
    };
  }

  componentDidMount() {
    axios.get(`http://localhost:5000/painting`).then((res) => {
      const galleries = res.data;
      this.setState({ galleries });
      console.log(galleries);
    });
  }

  render() {
    return (
      <section className="gallery_art_body">
        <h2 className="gallery_art_head_title">Art Gallery</h2>
        {this.state.galleries?.map((paint, index) => (
          <div className="gallery_art_parent" key={index}>
            <div className="gallery_art_container">
              <div className="gallery_art_img_container">
                <img
                  className="gallery_art_img"
                  src="https://placekitten.com/400/400"
                  alt="cat"
                />
              </div>

              <div className="gallery_art_child_container">
                <h3 className="gallery_art_title">{paint.title}</h3>
                <p className="gallery_art_paragraph">

                 {paint.description}
                  Lorem Ipsum is simply dummy text of <br /> the printing and
                  typesetting industry.
                  <br /> Lorem Ipsum has been the industry's <br />
                  standard dummy text ever since the <br /> 1500s, software like
                  Aldus PageMaker <br /> including versions of Lorem Ipsum.
                  <br />
                  Lorem Ipsum has been the industry's <br />
                  standard dummy text ever since the
                  <br /> 1500s, software like Aldus PageMaker
                  <br />
                  including versions of Lorem Ipsum
                </p>
              </div>
            </div>

            <div className="gallery_art_container">
              <div className="gallery_art_img_container">
                <img
                  className="gallery_art_img"
                  src="https://placekitten.com/400/400"
                  alt="cat"
                />
              </div>

              <div className="gallery_art_child_container">
                <h3 className="gallery_art_title">Title</h3>
                <p className="gallery_art_paragraph">
                  Lorem Ipsum is simply dummy text of <br /> the printing and
                  typesetting industry.
                  <br /> Lorem Ipsum has been the industry's <br />
                  standard dummy text ever since the <br /> 1500s, software like
                  Aldus PageMaker <br /> including versions of Lorem Ipsum.
                  <br />
                  Lorem Ipsum has been the industry's <br />
                  standard dummy text ever since the
                  <br /> 1500s, software like Aldus PageMaker
                  <br />
                  including versions of Lorem Ipsum
                </p>
              </div>
            </div>
          </div>
        ))}
      </section>
    );
  }
}

export default GalleryBody;
