import React from "react";
import axios from "axios";

class GalleryBody extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      galleries: [],
      show: false,

    };
  }




  componentDidMount = () => {
    axios
      .get("http://localhost:5000/painting")
      .then(res => {
        console.log(res);
        this.setState({ galleries: res.data.message });
      })
      .catch(err => console.log(err));
    this.setState({
      show: !this.state.show,
    });
  };





  render() {
    return (
      <section className="gallery_art_body">

      {console.log("test" , this.state.galleries)}

        <h2 className="gallery_art_head_title">Art Gallery</h2>



<div>
  {         
             this.state.galleries?.map((gallery , index )=> (
          <div className="gallery_art_parent" key={index} >
             <div className="gallery_art_container">
               <div className="gallery_art_img_container">
                 <img
                  className="gallery_art_img"
                  src= {`http://localhost:5000/uploads`.gallery.image}
                  alt="cat"
                />
              </div>

              <div className="gallery_art_child_container">
                <h3 className="gallery_art_title">{gallery.title}</h3>
                <p className="gallery_art_paragraph">

                 {gallery.description}

