import "./login_body.css";
import React from "react";
import axios from "axios";
import cookie from "react-cookies";
import { redirect, Route, Routes } from "react-router-dom";
import Visitor from "../../route/visitor/visitor";

class LoginBody extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      email: "",
      password: "",
      user: [],
      err: ""
    };
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }
  componentDidMount() {

  }
  handleChange(event) {
    this.setState({ [event.target.name]: event.target.value });
  }

  handleSubmit(event) {
    event.preventDefault();
    axios
      .post("http://localhost:5000/user/login", {
        email: this.state.email,
        password: this.state.password,
      })
      .then(function (response) {
        cookie.save("user", JSON.stringify(response.data.user), {
          maxAge: 5 * 60 * 60 * 1000,
        });
        cookie.save("auth_token", response.data.token, {
          maxAge: 5 * 60 * 60 * 1000,
        });
      })
      .catch(function (error) {
        // this.setState({ err: error });

        console.log(error);
      });

    // axios
    //   .get("http://localhost:5000/user/is-logged-in", {
    //     headers: { auth_token: cookie.load("auth_token") },
    //   })
    //   .then(function (response) {
    //     console.log(response.data);
    //   })
    //   .catch(function (error) {

    //     console.log(error.message);
    //   });
    // console.log(cookie.load("user"));
    // let user = cookie.load("user");
    // console.log(user.role);
  }

  render() {
    return (
      <div className="login_form">
        <h1>Login</h1>

        <form onSubmit={this.handleSubmit}>
          <div className="login_input_container">
            <label className="login_label">Email </label>
            <input
              className="login_Input"
              type="email"
              name="email"
              onChange={this.handleChange}
              required
            />
          </div>
          <div className="login_input_container">
            <label className="login_label">Password </label>
            <input
              className="login_Input"
              type="password"
              name="password"
              onChange={this.handleChange}
              required
            />
          </div>
          <div className="login_button_container">
            <input className="login_button" type="submit" value="Login" />
          </div>
        </form>
      </div>
    );
  }
}

export default LoginBody;
