import "./art_section_home.css";
import React from "react";
import { Link } from "react-router-dom";
import axios from "axios";

class ArtSectionHome extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      painting: [],
    };
  }

  componentDidMount() {
    axios.get(`http://localhost:5000/painting`).then((res) => {
      const painting = res.data;
      // this.setState({ painting });
      // console.log(painting);
      this.setState({ painting: res.data.results });
    });
  }

  render() {
    return (
      <section className="home_art_body">
        <h2 className="home_art_head_title">Some Artwork</h2>
        {console.log(this.painting)}

        {this.state.painting && this.state.painting?.map((paint, index) => (

          <div className="home_art_parent" key={index}>
            <div className="home_art_container">
              <div className="home_art_img_container">
                <img
                  className="home_art_img"
                  // src= {`http://localhost:5000/uploads`.paint.image}
                  alt="Art"
                />
              </div>

              <div className="home_art_child_container">
                <h3 className="home_art_title"> {paint.title}</h3>

                <p className="home_art_paragraph">
                  {paint.description}
                  Lorem Ipsum is simply dummy text of <br /> the printing and
                  typesetting industry.
                  <br /> Lorem Ipsum has been the industry's <br />
                  standard dummy text ever since the <br /> 1500s, software like
                  Aldus PageMaker <br /> including versions of Lorem Ipsum.
                  <br />
                  Lorem Ipsum has been the industry's <br />
                  standard dummy text ever since the
                  <br /> 1500s, software like Aldus PageMaker
                  <br />
                  including versions of Lorem Ipsum
                </p>
              </div>
            </div>

            <div className="home_art_container">
              <div className="home_art_img_container">
                <img
                  className="home_art_img"
                  // src= {`http://localhost:5000/uploads`.paint.image}
                  alt="Art"
                />
              </div>

              <div className="home_art_child_container">
                <h3 className="home_art_title"> {paint.title}</h3>
                <p className="home_art_paragraph">
                  {paint.description}
                  {/* Lorem Ipsum is simply dummy text of <br /> the printing and
                  typesetting industry.
                  <br /> Lorem Ipsum has been the industry's <br />
                  standard dummy text ever since the <br /> 1500s, software like
                  Aldus PageMaker <br /> including versions of Lorem Ipsum.
                  <br />
                  Lorem Ipsum has been the industry's <br />
                  standard dummy text ever since the
                  <br /> 1500s, software like Aldus PageMaker
                  <br />
                  including versions of Lorem Ipsum */}
                </p>
              </div>
            </div>
          </div>
        ))}

        <Link to="/gallery" className="home_art_btn">
          Show More
        </Link>
      </section>
    );
  }
}

export default ArtSectionHome;
