   const () {
    axios
    .get("http://localhost:5000/painting")
    .then(res => {
      console.log(res);
      this.setState({ painting: res.data.message });
    })
    .catch(err => console.log(err));
  this.setState({
    show: !this.state.show,
  });
   }
  componentDidMount = () => {
    fetching_data()
