import "./hero_section_home.css";
import React from "react";
import { Link } from "react-router-dom";
// import Paris from '/assets/Paris.mp4';

class HeroSectionHome extends React.Component {
  constructor(props) {
    super(props);
    this.state = {};
  }
  render() {
    return (
      <body className="home_hero_body">
        <main class="">
          <section class="home_hero_section">
            <div class="home_hero_container home_hero_row">
              <div className="home_hero_wrap_top">
                <h1 className="home_hero_title">
                  <span className="home_hero_accent_one">Square</span>{" "}
                  <span className="home_hero_accent_two">Art</span>
                </h1>
              </div>
              <div class="home_wrap_bottom">
                <p class="home_hero_text">
                  The largest community of arts enthusiasts.
                </p>
                <div>
                  <Link
                    to="/register"
                    class="home_hero_link  home_hero_link_button"
                  >
                    Join Today
                  </Link>
                </div>
              </div>
            </div>
          </section>
        </main>
      </body>
    );
  }
}

export default HeroSectionHome;
