import "./hero_section_home.css";
import React from "react";
import { Link } from "react-router-dom";

class HeroSectionHome extends React.Component {
  constructor(props) {
    super(props);
    this.state = {};
  }
  render() {
    return (
      <body className="home-hero-body">
        <main class="">
          <section class="home-hero-section">
            <div class="home-hero-container home-hero-row">
              <h1 class="hero__title">Art <accent>Gallery</accent> </h1>
              <div class="wrap wrap--bottom">
                <p class="hero__text">
                  The largest community of arts enthusiasts.
                </p>



                <div>
                  <Link to="/register" class="hero__link  link-button">
                    Join Today
                  </Link>
                </div>
              </div>
            </div>
          </section>
        </main>
      </body>
    );
  }
}

export default HeroSectionHome;
